import React, { useEffect, useState } from 'react'
import {useNavigate, useLocation} from "react-router-dom"

export default function MovieDetails({navigation}) {
  const[characters, setCharacters] = useState([])
  const[starships, setStarships] = useState([])
  const[planets, setPlanets] = useState([])

  const navigate = useNavigate();
  const location = useLocation();
  const movie = location.state

  useEffect(()=>{
    console.log("data coming", movie)
    getAllCharacters()
    getAllStarships()
    getPlanets()
    // getCharacters()
    // getStarships()
    // getPlanets()
  },[])

  async function getAllCharacters(){
    movie.characters.map((c)=>{
      getCharacters(c)
    })  
  }

  async function getCharacters(url){
    await fetch(url)
          .then(response => response.json())
          .then(res => {
            console.log("character respnse", res)
            setCharacters((prevState) => [...prevState , res])
          })
          .catch((error) => console.log(error));
  }

  async function getAllStarships(){
    movie.starships.map((s)=>{
      getStarships(s)
    })  
  }

  async function getStarships(url){
    await fetch(url)
          .then(response => response.json())
          .then(res => {
            console.log("character respnse", res)
            setStarships((prevState) => [...prevState , res])
          })
          .catch((error) => console.log(error));
  }

  async function getAllStarships(){
    movie.starships.map((s)=>{
      getStarships(s)
    })  
  }
  async function getPlanets(){
    await fetch(`https://swapi.dev/api/planets/4/`)
          .then(response => response.json())
          .then(res => {
            console.log("planet respnse", res)
            // setData(res)
          })
          .catch((error) => console.log(error));
  }

  useEffect(()=>{
    console.log("characters", characters)
  },[characters])

  return (
    <div>
      <h1>{movie.title}</h1>
      {/* <h2>{character}</h2> */}

      <button title="Actor" onClick={()=> navigate('/actor')}/>
      <button title="Planet" onClick={()=> navigate('/planet')}/>
      <button title="Startship" onClick={()=> navigate('/starship')}/>

    </div>
  )
}
