import React, { useEffect, useState } from 'react'
import {useNavigate, useLocation} from "react-router-dom"

export default function MovieDetails({navigation}) {
  const[characters, setCharacters] = useState([])
  const navigate = useNavigate();
  const location = useLocation();
  const movie = location.state

  useEffect(()=>{
    console.log("data coming", movie)
    getAllData()
    // getCharacters()
    // getStarships()
    // getPlanets()
  },[])

  async function getAllData(){
    movie.characters.map((c)=>{
      getCharacters(c)
    })  
  }

  async function getCharacters(url){
    await fetch(url)
          .then(response => response.json())
          .then(res => {
            console.log("character respnse", res)
            setCharacters((prevState) => prevState + res)
            // setData(res)
          })
          .catch((error) => console.log(error));
  }

  async function getStarships(){
    await fetch(`https://swapi.dev/api/starships/3/`)
          .then(response => response.json())
          .then(res => {
            console.log("starships respnse", res)
            // setData(res)
          })
          .catch((error) => console.log(error));
  }

  async function getPlanets(){
    await fetch(`https://swapi.dev/api/planets/4/`)
          .then(response => response.json())
          .then(res => {
            console.log("planet respnse", res)
            // setData(res)
          })
          .catch((error) => console.log(error));
  }
  return (
    <div>
      <h1>{movie.title}</h1>
      {/* <h2>{character}</h2> */}

      <button title="Actor" onClick={()=> navigate('/actor')}/>
      <button title="Planet" onClick={()=> navigate('/planet')}/>
      <button title="Startship" onClick={()=> navigate('/starship')}/>

    </div>
  )
}
