import React, { useEffect, useState, useCallback } from 'react'
import {useNavigate, useLocation} from "react-router-dom"
import { Button, Grid,TextField, Typography ,Link,Skeleton,Box } from '@mui/material';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { makeStyles } from '@mui/styles'

const useStyles = makeStyles((theme) => ({
  heading:{
    padding:"20px",
    color:"#E74C3C",
    fontFamily:"Montserrat !important",
    textTransform:"uppercase",
  },
  root:{
    padding:"20px",
    display:"flex",
    flexDirection:"row",
    justifyContent:"flex-start",
    height:"100vh"
  },
  card:{
    backgroundColor:"#000",
    borderRadius:"8px",
    height:"60vh",
    width:"20vw !important",
    padding:"10px",
    margin:"20px",
    flexDirection:"column !important",
    justifyContent:"flex-start"
  },
  title:{
    color:"#FFF",
    fontFamily:"Montserrat !important",
    textTransform:"uppercase",
  },
  key:{
    color:"#FFF",
    fontFamily:"Montserrat !important",
    textTransform:"capitalize",
    padding:"10px",
    textDecoration:"underline"
  },
  value:{
    color:"#FFF",
    fontFamily:"Montserrat !important",
    textTransform:"capitalize",
    padding:"15px",
  },
  releaseDate:{
    color:"#E74C3C",
    fontFamily:"Montserrat !important",
    textTransform:"capitalize",
  },
  buttonContainer:{
    height:"5%",
    flexDirection:'row',
    justifyContent:"flex-end",
    alignItems:"center"
  },
  head:{
    height:"23%",
    flexDirection:"column !important",
    justifyContent:"flex-start"
  },
  body:{
    height:"66%",
    flexDirection:"column !important",
    justifyContent:"flex-start",
    marginTop:"10px"
  },
  button:{
    color:"#E74C3C !important ",
  }
}))

export default function MovieDetails({navigation}) {
  const[characters, setCharacters] = useState([])
  const[starships, setStarships] = useState([])
  const[planets, setPlanets] = useState([])

  const navigate = useNavigate();
  const location = useLocation();
  const movie = location.state

  useEffect(()=>{
    console.log("data coming", movie)
    getAllCharacters()
    getAllStarships()
    getAllPlanets()
  },[])

  async function getAllCharacters(){
    movie.characters.map((c)=>{
      getCharacters(c)
    })  
  }

  async function getCharacters(url){
    await fetch(url)
          .then(response => response.json())
          .then(res => {
            console.log("character respnse", res)
            setCharacters((prevState) => [...prevState , res])
          })
          .catch((error) => console.log(error));
  }

  async function getAllStarships(){
    movie.starships.map((s)=>{
      getStarships(s)
    })  
  }

  async function getStarships(url){
    await fetch(url)
          .then(response => response.json())
          .then(res => {
            console.log("starships respnse", res)
            setStarships((prevState) => [...prevState , res])
          })
          .catch((error) => console.log(error));
  }

  async function getAllPlanets(){
    movie.planets.map((p)=>{
      getPlanets(p)
    })  
  }
  async function getPlanets(url){
    await fetch(url)
          .then(response => response.json())
          .then(res => {
            console.log("planet respnse", res)
            setPlanets((prevState) => [...prevState , res])
          })
          .catch((error) => console.log(error));
  }

  const openCharacter = useCallback((id) => () => {
    console.log("character", id)
    navigate('/actor',{state: id})
  },
  [],
)

  const openStarship = useCallback((id) => () => {
    console.log("starship", id)
    navigate('/starship',{state: id})
  },
  [],
)

  const openPlanet = useCallback((id) => () => {
    console.log("planet", id)
    navigate('/planet',{state: id})
  },
  [],
)
  return (
    <div>
      <h1>{movie.title}</h1>
      <h2>characters</h2>
      {characters.map((item, index)=>(
        <Grid className={classes.card} container>
          <Grid className={classes.head} container>
            <Typography variant="h4" className={classes.title}>{item.title}</Typography>
          </Grid>
          <Grid className={classes.body} container>
            <Typography variant="body" className={classes.releaseDate}>( {item.release_date.slice(0,4)} )</Typography>

            <Typography variant="body" className={classes.key}>Director  :-</Typography>
            <Typography variant="body" className={classes.value}>{item.director}</Typography>
            <Typography variant="body" className={classes.key}>Producers  :-</Typography>
            <Typography variant="body" className={classes.value}>{item.producer}</Typography>
          </Grid>
          <Grid className={classes.buttonContainer}>
            <Button onClick={handleClick(item)} variant="text" className={classes.button} > View Details  <ChevronRightIcon/> </Button>
          </Grid>
        </Grid>
        // <button onClick={openCharacter(item)} key={index}>{item.name}</button >
      ))}

      <h2>starships</h2>

      {starships.map((item, index)=>(
        <button onClick={openStarship(item)} key={index}>{item.name}</button >
      ))}
        <h2>planets</h2>

      {planets.map((item, index)=>(
        <button onClick={openPlanet(item)} key={index}>{item.name}</button >
      ))}
      <button title="Actor" onClick={()=> navigate('/actor')}/>
      <button title="Planet" onClick={()=> navigate('/planet')}/>
      <button title="Startship" onClick={()=> navigate('/starship')}/>

    </div>
  )
}
