import React, { useEffect, useState, useCallback } from 'react'
import {useNavigate, useLocation} from "react-router-dom"
import Person3RoundedIcon from '@mui/icons-material/Person3Rounded';
import { Button, Grid,TextField, Typography ,Link,Skeleton,Box,Card } from '@mui/material';
import { makeStyles } from '@mui/styles'

const useStyles = makeStyles((theme) => ({
root:{
  flexDirection:"column",
  justifyContent:"flex-start",
  padding:"20px"
},
headContainer:{

},
card:{
  height:"25vh",
  width:"10vw",
  alignItems:"center",
  justifyContent:"center",
  backgroundColor:"#FFF",
  borderRadius:"6px !important"
},
title:{
  fontSize: "40px !important",
  fontWeight:"bold !important"
},
detail:{
  fontSize: "20px !important",
  fontWeight:"400 !important",
  fontStyle: "italic",
  color:""
}
}))

export default function ActorDetails() {
  const navigate = useNavigate();
  const location = useLocation();
  const character = location.state
  const classes = useStyles()


  return (
    <div>
      <Grid className={classes.root}>
        <Grid className={classes.headContainer} container>
          <Grid item xs={2}>
            <Card className={classes.card} container>
              <Person3RoundedIcon sx={{height: "100%", width: "100%", color:"#7E7A7A"}}/>
            </Card>
          </Grid>
          <Grid item xs={3}>
            <Typography variant="h5" className={classes.title}>{character.name}</Typography>
            <Typography variant="body" className={classes.detail}>{character.name}</Typography>
          </Grid>
        </Grid>
      </Grid>
        {/* <button >{character.name}</button > */}
      </div>
  )
}
